import axios from "axios";
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const Fixtures = () => {
  const [results, setResults] = useState([]);
  const [search, setSearch] = useState("");
  const [selected, setSelected] = useState(null);
  const [bookDetails, setBookDetails] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`https://openlibrary.org/search.json?q=${search}`);
        if (response.data && response.data.docs && search === response.data.q) {
          setResults(response.data.docs);
        }
      } catch (error) {
        console.log(error);
      }
    };

    if (search.trim() !== "") {
      fetchData();
    } else {
      setResults([]);
    }
  }, [search]);

  

  const selection = (book) => {
    setSelected(book);
    setBookDetails(null); // Reset bookDetails when a new book is selected
    fetchBookDetails();
  }

  return (
    <div className="main-container">
      <h1 className="heading1">UMAIR OPEN LIBRARYY</h1>
      <input
        style={{ backgroundColor: "rgba(148 , 158 , 171 , 0.04)" }}
        id="search"
        type="text"
        className="form-control"
        placeholder="Search"
        autoComplete="off"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      <label htmlFor="search"></label>
      <div>
        {/* Render results */}
        {results.length > 0 ? (
          <ul>
            {results.map((result, index) => (
              <li key={index} onClick={() => selection(result)}>{result.title}</li>
            ))}
          </ul>
        ) : (
          <p>No results found.</p>
        )}
      </div>

      <div className="link">
        {/* Check if selected is not null before rendering */}
        {selected !== null && (
          <div>
            <h1>{selected.title}</h1>
            {bookDetails && (
              <div>
                <h2>Story:</h2>
                <p>{bookDetails.story}</p>
                {/* Render other detailed information as needed */}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Fixtures;
