import axios from "axios";
import React, { useState, useEffect } from "react";
import './App.css';

const Fixtures = () => {
  const [results, setResults] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`https://openlibrary.org/search.json?q=${search}`);
        // Check if response has data and if it matches the current search query
        if (response.data && response.data.docs && search === response.data.q) {
          setResults(response.data.docs);
          console.log(response.data)
        }
      } catch (error) {
        console.log(error);
        // Handle error if needed
      }
    };

    // Fetch data only if search is not empty
    if (search.trim() !== "") {
      fetchData();
    } else {
      // Clear results if search is empty
      setResults([]);
    }
  }, [search]);

  return (
    <div className="main-container">
      <h1 className="heading1">UMAIR OPEN LIBRARYY</h1>
      <input
        style={{ backgroundColor: "rgba(148 , 158 , 171 , 0.04)" }}
        id="search"
        type="text"
        className="form-control"
        placeholder="Search"
        autoComplete="off"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      <label htmlFor="search"></label>
      <div>
        {/* Render results */}
        {results.length > 0 ? (
          <ul>
            {results.map((result, index) => (
              <li key={index}>{result.title}</li>
            ))}
          </ul>
        ) : (
          <p>No results found.</p>
        )}
      </div>
    </div>
  );
};

export default Fixtures;
