import axios from "axios";
import React, { useState, useEffect } from "react";

const Fixtures = () => {
  const [results, setResults] = useState([]);
  const [search, setSearch] = useState("");
  const [selected, setSelected] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`https://www.googleapis.com/books/v1/volumes?q=${search}&key=YOUR_API_KEY`);
        if (response.data && response.data.items) {
          setResults(response.data.items);
        }
      } catch (error) {
        console.log(error);
      }
    };

    if (search.trim() !== "") {
      fetchData();
    } else {
      setResults([]);
    }
  }, [search]);

  const selection = (book) => {
    setSelected(book);
  };

  return (
    <div className="main-container">
      <h1 className="heading1">UMAIR OPEN LIBRARY</h1>
      <input
        style={{ backgroundColor: "rgba(148, 158, 171, 0.04)" }}
        id="search"
        type="text"
        className="form-control"
        placeholder="Search"
        autoComplete="off"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      <label htmlFor="search"></label>
      <div>
        {/* Render results */}
        {results.length > 0 ? (
          <ul>
            {results.map((result, index) => (
              <li key={index} onClick={() => selection(result)}>{result.volumeInfo.title}</li>
            ))}
          </ul>
        ) : (
          <p>No results found.</p>
        )}
      </div>

      <div className="link">
        {/* Display selected book details */}
        {selected && (
          <div>
            <h2>{selected.volumeInfo.title}</h2>
            <p>{selected.volumeInfo.description}</p>
            {/* You can render more details as needed */}
          </div>
        )}
      </div>
    </div>
  );
};

export default Fixtures;
